mapper.update:
UPDATE bookmark_tag SET sort_order = CASE id
WHEN #{tag.id} THEN #{tag.sortOrder}
END WHERE id IN
#{tag.id}

mapper.select:
SELECT COUNT(1)
FROM bookmark_url_tag
WHERE tag_id = #{tagId}

SELECT COUNT(DISTINCT url_id)
FROM bookmark_url_tag
WHERE tag_id = #{parentTagId}
   OR tag_id IN (
       SELECT id 
       FROM bookmark_tag 
       WHERE parent_id = #{parentTagId}
   )
