mapper.select:
SELECT

FROM bookmark_url
WHERE id = #{id}
    AND user_id = #{userId}
LIMIT 1

SELECT DISTINCT

FROM bookmark_url u
INNER JOIN bookmark_url_tag ut ON u.id = ut.url_id
WHERE ut.tag_id = #{tagId}
    AND u.user_id = #{userId}
ORDER BY u.created_at DESC

SELECT DISTINCT
FROM bookmark_url u WHERE u.user_id = #{userId} AND EXISTS ( SELECT 1 FROM bookmark_url_tag ut WHERE ut.url_id = u.id AND ut.tag_id IN
#{tagId}
) ORDER BY u.created_at DESC

mapper.delete:
DELETE FROM bookmark_url 
WHERE id = #{id}
    AND user_id = #{userId}

DELETE FROM bookmark_url
WHERE user_id = #{userId}
    AND id IN

        #{id}
