mapper.select:
SELECT

FROM health_training_plan
WHERE id = #{id}
  AND user_id = #{userId}
LIMIT 1

SELECT

FROM health_training_plan
WHERE user_id = #{userId}

    AND status = #{status}


    AND plan_type = #{planType}

ORDER BY created_at DESC

SELECT COUNT(1)
FROM health_exercise_record
WHERE training_plan_id = #{planId}
  AND user_id = #{userId}

mapper.delete:
DELETE FROM health_training_plan
WHERE id = #{id}
  AND user_id = #{userId}
