mapper.select:
SELECT

FROM todo_module
WHERE id = #{id}
  AND user_id = #{userId}
LIMIT 1

SELECT

FROM todo_module
WHERE user_id = #{userId}
  AND name = #{name}
LIMIT 1

mapper.delete:
DELETE FROM todo_module
WHERE id = #{id}
  AND user_id = #{userId}

mapper.select:
SELECT COUNT(1)
FROM todo_task
WHERE module_id = #{moduleId}
  AND user_id = #{userId}
