mapper.select:
SELECT id, user_id, name, parent_id, level, sort_order, created_at, updated_at FROM bookmark_tag WHERE name = #{name} AND user_id = #{userId}
AND parent_id IS NULL
AND parent_id = #{parentId}
LIMIT 1

mapper.update:
UPDATE bookmark_tag
SET
    name = #{name},
    parent_id = #{parentId},
    level = #{level},
    sort_order = #{sortOrder},
    updated_at = #{updatedAt}
WHERE
    id = #{id}
    AND user_id = #{userId}

mapper.delete:
DELETE FROM bookmark_tag 
WHERE id = #{id}
    AND user_id = #{userId}

mapper.update:
UPDATE bookmark_tag
SET
    sort_order = CASE id

        WHEN #{tag.id} THEN #{tag.sortOrder}

    END
WHERE
    user_id = #{userId}
    AND id IN

        #{tag.id}

mapper.select:
SELECT COUNT(1)
FROM bookmark_url_tag ut
INNER JOIN bookmark_url u ON ut.url_id = u.id
WHERE ut.tag_id = #{tagId}
    AND u.user_id = #{userId}

SELECT COUNT(DISTINCT ut.url_id)
FROM bookmark_url_tag ut
INNER JOIN bookmark_url u ON ut.url_id = u.id
WHERE u.user_id = #{userId}
    AND (ut.tag_id = #{parentTagId}
        OR ut.tag_id IN (
            SELECT id 
            FROM bookmark_tag 
            WHERE parent_id = #{parentTagId}
                AND user_id = #{userId}
        ))
