  AND user_id = #{userId}
LIMIT 1

SELECT

FROM health_exercise_record
WHERE user_id = #{userId}

    AND exercise_type IN

        #{type}



    AND exercise_date >= #{startDate}


    AND exercise_date <= #{endDate}


    AND training_plan_id = #{trainingPlanId}



        ORDER BY

            exercise_date
            duration_minutes
            calories_burned
            exercise_date


            ASC
            DESC



        ORDER BY exercise_date DESC


LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(1)
FROM health_exercise_record
WHERE user_id = #{userId}

    AND exercise_type IN

        #{type}



    AND exercise_date >= #{startDate}


    AND exercise_date <= #{endDate}


    AND training_plan_id = #{trainingPlanId}

mapper.delete:
DELETE FROM health_exercise_record
WHERE id = #{id}
  AND user_id = #{userId}

DELETE FROM health_exercise_record
WHERE user_id = #{userId}
  AND id IN

      #{id}

mapper.select:
SELECT
    COALESCE(SUM(duration_minutes), 0) AS totalDuration,
    COUNT(1) AS totalCount,
    COALESCE(SUM(calories_burned), 0) AS totalCalories,
    COALESCE(SUM(distance_km), 0) AS totalDistance
FROM health_exercise_record
WHERE user_id = #{userId}

    AND exercise_date >= #{startDate}


    AND exercise_date <= #{endDate}

SELECT exercise_type AS exerciseType, COUNT(1) AS count, COALESCE(SUM(duration_minutes), 0) AS totalDuration, COALESCE(SUM(calories_burned), 0) AS totalCalories, COALESCE(SUM(distance_km), 0) AS totalDistance FROM health_exercise_record WHERE user_id = #{userId}
AND exercise_date >= #{startDate}
AND exercise_date <= #{endDate}
GROUP BY exercise_type ORDER BY count DESC

SELECT e.training_plan_id AS planId, p.plan_name AS planName, COUNT(1) AS count, COALESCE(SUM(e.duration_minutes), 0) AS totalDuration, COALESCE(SUM(e.calories_burned), 0) AS totalCalories FROM health_exercise_record e LEFT JOIN health_training_plan p ON e.training_plan_id = p.id AND e.user_id = p.user_id WHERE e.user_id = #{userId} AND e.training_plan_id IS NOT NULL
AND e.exercise_date >= #{startDate}
AND e.exercise_date <= #{endDate}
GROUP BY e.training_plan_id, p.plan_name ORDER BY count DESC

SELECT DISTINCT exercise_date
FROM health_exercise_record
WHERE user_id = #{userId}

    AND exercise_date >= #{startDate}


    AND exercise_date <= #{endDate}

ORDER BY exercise_date ASC

