mapper.select:
SELECT

FROM health_water_intake
WHERE id = #{id}
  AND user_id = #{userId}
LIMIT 1

SELECT

FROM health_water_intake
WHERE user_id = #{userId}

    AND water_type IN

        #{type}



    AND intake_date >= #{startDate}


    AND intake_date <= #{endDate}



        ORDER BY

            intake_date
            intake_time
            volume_ml
            intake_date


            ASC
            DESC



        ORDER BY intake_date DESC, intake_time DESC


LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(1)
FROM health_water_intake
WHERE user_id = #{userId}

    AND water_type IN

        #{type}



    AND intake_date >= #{startDate}


    AND intake_date <= #{endDate}

mapper.insert:
INSERT INTO health_water_intake (
    id, user_id, intake_date, intake_time, water_type, volume_ml, notes, created_at, updated_at
) VALUES (
    #{id}, #{userId}, #{intakeDate}, #{intakeTime}, #{waterType}, #{volumeMl}, #{notes},
    IFNULL(#{createdAt}, NOW()), IFNULL(#{updatedAt}, NOW())
)

mapper.delete:
DELETE FROM health_water_intake
WHERE id = #{id}
  AND user_id = #{userId}

DELETE FROM health_water_intake
WHERE user_id = #{userId}
  AND id IN

      #{id}

mapper.select:
SELECT COALESCE(SUM(volume_ml), 0)
FROM health_water_intake
WHERE user_id = #{userId}

    AND intake_date >= #{startDate}


    AND intake_date <= #{endDate}

SELECT
    COALESCE(SUM(volume_ml), 0) AS totalIntakeMl,
    COUNT(1) AS totalCount
FROM health_water_intake
WHERE user_id = #{userId}

    AND intake_date >= #{startDate}


    AND intake_date <= #{endDate}

SELECT COALESCE(SUM(volume_ml), 0)
FROM health_water_intake
WHERE user_id = #{userId}
  AND intake_date = #{intakeDate}

SELECT
    water_type AS waterType,
    COUNT(1) AS count,
    COALESCE(SUM(volume_ml), 0) AS totalVolume
FROM health_water_intake
WHERE user_id = #{userId}

    AND intake_date >= #{startDate}


    AND intake_date <= #{endDate}

GROUP BY water_type
ORDER BY totalVolume DESC

SELECT DISTINCT intake_date
FROM health_water_intake
WHERE user_id = #{userId}

    AND intake_date >= #{startDate}


    AND intake_date <= #{endDate}

ORDER BY intake_date ASC

SELECT
FROM health_water_intake WHERE user_id = #{userId}
AND water_type IN
#{type}
AND intake_date >= #{startDate}
AND intake_date <= #{endDate}
ORDER BY
intake_date intake_time volume_ml intake_date
ASC DESC
ORDER BY intake_date DESC, intake_time DESC
