mapper.select:
select

from sys_user_config
where user_id = #{userId}
  and module = #{module}
  and config_key = #{configKey}
limit 1

select count(1)
from sys_user_config
where user_id = #{userId}

mapper.update:
update sys_user_config
set
    config_value = #{configValue},
    description = #{description},
    updated_at = now(),
    updated_by = #{updatedBy}
where id = #{id}
  and user_id = #{userId}
